mapper.select:
select * from customer



			status is null


			status = #{status}

mapper.update:
update customer set password = #{password}, name = #{name} ,
 status = ''
 status = #{status}
where id = #{id}

mapper.select:
select * from
(select rownum as rnum, a.*
from customer a


		id like '%' || #{id} || '%'


		 name like '%' || #{name} || '%'


		status = #{status}


)
WHERE rnum between #{startRow} AND #{endRow}
order by id desc
